mapper.select:
SELECT h.hname as house_name, co.name, co.description, co.price, p.createTime
FROM house h,pay_orders p, commodity co
WHERE p.uid = #{userId} and h.id = p.hid and co.id = p.commodityId ORDER BY p.createTime DESC;

SELECT h.hname as house_name, co.name, co.description, co.price, p.createTime
FROM house h,pay_orders p, commodity co
WHERE p.uid = #{userId} and h.id = p.hid and p.commodityId = #{commodityId} and co.id = p.commodityId ORDER BY p.createTime DESC ;
